-- Create USERS table with auto generated "id" column
-- Note: Oracle will auto create a sequence for "id" genration
CREATE TABLE USERS
(
    id        NUMBER GENERATED BY DEFAULT ON NULL AS IDENTITY PRIMARY KEY,
    firstName VARCHAR2(100),
    lastName  VARCHAR2(100),
    email     VARCHAR2(255)
);

